mapper.select:

select * from 
(select * from
(select p.p_id pid,p_id,p.p_name,p.p_sex,p_type,p_identily,p.p_phone,p.p_f_id,p.p_order_id from passenger p)p
	left join
	flight_p  f
	on p.p_f_id=f.f_id)pf
left join 
order_p o
on pf.p_order_id=o.o_id


	pf.f_location=#{location}
	
	and pf.f_departure=#{departure}
	
	and pf.p_name=#{passengerName}
	
	and pf.p_id=#{PNR}
	
	and pf.f_company=#{Airlines}
	
	and o.o_state=#{ticketState}
	
	and DATE_FORMAT(f_starttime,'%yyyy-%MM-%dd')  >=  DATE_FORMAT(#{ticketTime1},'%yyyy-%MM-%dd')  
	
	and  DATE_FORMAT(f_starttime,'%yyyy-%MM-%dd') <=  DATE_FORMAT(#{ticketTime2},'%yyyy-%MM-%dd')    

	and DATE_FORMAT(o_paytime,'%yyyy-%MM-%dd')  >=  DATE_FORMAT(#{payTime1},'%yyyy-%MM-%dd')  
	
	and  DATE_FORMAT(o_paytime,'%yyyy-%MM-%dd') <=  DATE_FORMAT(#{payTime2},'%yyyy-%MM-%dd')


select * from 
(select * from
(select p.p_id pid,p_id,p.p_name,p.p_sex,p_type,p_identily,p.p_phone,p.p_f_id,p.p_order_id from passenger p)p
	left join
	flight_p  f
	on p.p_f_id=f.f_id)pf
left join 
order_p o
on pf.p_order_id=o.o_id


	pf.f_location=#{location}
	
	and pf.f_departure=#{departure}
	
	and pf.p_name=#{passengerName}
	
	and pf.p_id=#{PNR}
	
	and pf.f_company=#{Airlines}
	
	and o.o_state=#{ticketState}
	
	and DATE_FORMAT(f_starttime,'%yyyy-%MM-%dd')  >=  DATE_FORMAT(#{ticketTime1},'%yyyy-%MM-%dd')  
	
	and  DATE_FORMAT(f_starttime,'%yyyy-%MM-%dd') <=  DATE_FORMAT(#{ticketTime2},'%yyyy-%MM-%dd')
